$(function(){
    countdown((".countdown strong"));
  });
var countdown = function (countdown){
    getCycleCountdown();
      $(countdown).eq(0).html(d).end().eq(1).html(h).end().eq(2).html(i).end().eq(3).html(s);
      setTimeout("countdown('"+ countdown +"')", 1);
  };
  function getCycleCountdown () {
      var startTimestamp = MApp(2.2).datetime.getTimestamp('2020-12-31 00:00:00');
      var datetime = MApp(2.2).datetime.getCycleCountdown(startTimestamp, 0);
      d = MApp(2.2).packages.zeroize(datetime[0], 2);
      h = MApp(2.2).packages.zeroize(datetime[1], 2);
      i = MApp(2.2).packages.zeroize(datetime[2], 2);
      s = MApp(2.2).packages.zeroize(datetime[3], 2);
      mi = MApp(2.2).packages.zeroize(datetime[4], 3);
  }







